/* ==UserStyle==
@name         AI Studio Custom Styles (AoT Theme v20.5 - Refined Font Overrides)
@namespace    userstyles.world/user/yourname (or your preferred namespace)
@version      20.5
@description  Global blurred background. Distinct BORDERS. Refined Font Overrides (SF Pro Rounded, Targeted Sizes/Line Height). Fixes: Sidebar BG, Compare Btn BG, Run Btn text.
@author       YourName (Adapted from v20.4 & User Feedback)
@license      MIT (or your preferred license)
@homepageURL  https://github.com/your-repo (Optional: link to where you host it)
@supportURL   https://github.com/your-repo/issues (Optional: link for issues)
@updateURL    https://raw.githubusercontent.com/your-repo/master/aistudio-aot.user.css (Optional: for auto-updates)
==/UserStyle== */
@-moz-document url-prefix("https://aistudio.google.com/") {

    /* --- Values from Original Script ---
    const globalBgColor = 'rgba(16, 19, 28, 0.94)';
    const globalBgBlur = '30px';
    const overlayPanelBgColor = 'rgba(18, 21, 32, 0.82)';
    const overlayPanelBlur = '25px';
    const overlayPanelBorderColor = 'rgba(130, 130, 180, 0.35)';
    const overlayPanelBorderRadius = '0.8rem';
    const overlayPanelBoxShadow = '0 8px 30px rgba(0, 0, 0, 0.50)';
    const transparentPanelBorderColor = 'rgba(130, 130, 180, 0.30)';
    const transparentPanelBorderRadius = '0.8rem';
    const transparentPanelBoxShadow = '0 10px 35px rgba(0, 0, 0, 0.45)';
    const nestedPanelBlur = '18px';
    const llmBubbleBgColor = 'rgba(20, 24, 40, 0.90)';
    const llmBubbleBorderWidth = '1px';
    const llmBubbleBorderColor = 'rgba(130, 130, 180, 0.45)';
    const userPanelBgColor = 'rgba(28, 38, 60, 0.94)';
    const userPanelBorderWidth = '2px';
    const userPanelBorderColor = '#3c4768';
    const thoughtPanelBgColor = 'rgba(8, 10, 15, 0.97)';
    const thoughtPanelBorderWidth = '1px';
    const thoughtPanelBorderColor = '#45474a';
    const codeSysPanelBgColor = 'rgba(15, 17, 22, 0.95)';
    const codeSysPanelBorderWidth = '1px';
    const codeSysPanelBorderColor = '#3c4768';
    const primaryTextColor = '#ffffff';
    const secondaryTextColor = '#d5d5dc';
    const userMessageTextColor = '#ffffff';
    const codeBlockMetaColor = '#b0b5c0';
    const defaultFontFamily = '"SF Pro Rounded", "SF Pro Display", sans-serif';
    const paragraphFontSize = '20px';
    const headingFontSize = '22px';
    const globalLineHeight = '32px';
    const headingLineHeight = '28px';
    const codeFontSize = '18px';
    const codeLineHeight = '28px';
    --- End Original Values --- */
    /*********************************************************************
     * NOTE: The weird text replacements ("generating_tokens", "thermostat")
     * CANNOT be caused by CSS. CSS only styles elements, it doesn't
     * change text content. This is likely a temporary glitch in AI Studio,
     * a conflict with ANOTHER browser extension/script, or a browser issue.
     * Try disabling other extensions, clearing cache, or restarting browser.
     *********************************************************************/
    /* --- 0. FONT OVERRIDES (Refined) --- */
    /* Global Font Family */
    body {
        font-family: "SF Pro Rounded", "SF Pro Display", sans-serif !important;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
    }
    /* Force font on code elements */
    pre,
    code,
    ms-code-block pre code {
        font-family: "SF Pro Rounded", "SF Pro Display", sans-serif !important;
        /* Override monospace */
    }

    /* Paragraph-level text size & line height */
    p,
    .message-bubble,
    /* Model response */
    ms-prompt-chunk .text-chunk-content,
    /* User response */
    ms-cmark-node > *:not(h1):not(h2):not(h3):not(h4):not(h5):not(h6):not(pre):not(code):not(ul):not(ol):not(blockquote):not(table),
    /* Markdown text elements */
    ms-cmark-node > ul li,
    ms-cmark-node > ol li,
    /* Markdown lists */
    ms-cmark-node > blockquote p,
    /* Paragraphs inside blockquotes */
    .mat-mdc-dialog-content,
    /* Dialog text */
    .mat-mdc-list-item .mat-mdc-list-item-unscoped-content,
    /* List items in menus */
    .mat-mdc-option .mdc-list-item__primary-text,
    /* Select options */
    .mat-mdc-menu-item .mat-mdc-menu-item-text,
    /* Menu items */
    textarea,
    /* Input area */
    .system-instruction-panel .mat-expansion-panel-body,
    /* System instructions */
    ms-model-thought .content/* Thought content */
    {
        font-size: 20px !important;
        line-height: 32px !important;
    }

    /* Headings size & line height */
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    .mat-mdc-dialog-title,
    .gmat-title-medium,
    .gmat-title-large,
    /* General title classes */
    nav.sidenav .nav-section-header,
    /* Sidebar headers */
    .config-panel .setting-section .header,
    /* Config panel headers */
    .config-panel .mat-expansion-panel-header .mat-expansion-panel-header-title,
    /* Config panel expansion titles */
    .system-instruction-panel .mat-expansion-panel-header .mat-expansion-panel-header-title,
    /* Sys instr title */
    ms-model-thought .header/* Thought header */
    {
        font-size: 22px !important;
        line-height: 28px !important;
        font-weight: 600 !important;
    }

    /* Code Blocks size & line height */
    pre,
    code,
    ms-code-block pre code {
        font-size: 18px !important;
        line-height: 28px !important;
    }

    /* Button Text - Restore reasonable size & line height */
    button .mdc-button__label,
    a.mat-mdc-button .mdc-button__label {
        font-size: 16px !important;
        /* Or adjust as needed */
        line-height: normal !important;
        /* Let button height control this */
        font-weight: 500 !important;
    }
    /* Small text elements (like code meta info) - Restore reasonable size & line height */
    ms-action-chip-list span.language-name,
    ms-action-chip-list span.disclaimer,
    div.code-block-footer span {
        font-size: 14px !important;
        line-height: normal !important;
    }
    /* Sidebar list items - Restore reasonable size & line height */
    .mat-nav-list .mat-list-item .mat-list-item-content {
        font-size: 16px !important;
        /* Adjust if needed */
        line-height: normal !important;
    }
    /* Ensure icons don't inherit large line height */
    mat-icon,
    .material-symbols-outlined {
        line-height: 1 !important;
    }


    /* --- 1. Body Base & Background Layers --- */
    /* body font-family removed, handled globally */
    body {
        background-color: #0a0c10 !important;
        color: #ffffff !important;
        position: relative;
        min-height: 100vh;
        overflow: hidden !important;
    }
    body::before {
        /* Dots Layer */
        content: '';
        position: fixed;
        inset: 0;
        background-image: radial-gradient(rgba(255, 255, 255, 0.07) 1px, transparent 1px), radial-gradient(rgba(255, 255, 255, 0.03) 1.5px, transparent 1.5px);
        background-size: 28px 28px, 80px 80px;
        background-position: 0 0, 14px 14px;
        pointer-events: none;
        z-index: -2 !important;
        opacity: 0.9;
    }
    body::after {
        /* Blur Layer */
        content: '';
        position: fixed;
        inset: 0;
        background: rgba(16, 19, 28, 0.94) !important;
        backdrop-filter: blur(30px) !important;
        -webkit-backdrop-filter: blur(30px) !important;
        pointer-events: none;
        z-index: -1 !important;
    }

    /* --- 2. AGGRESSIVE GLOBAL Transparency & Variable Overrides --- */
    :root,
    body,
    .light-theme,
    .dark-theme {
        /* Variable Overrides */
        --color-sidebars-background: transparent !important;
        --color-canvas-background: transparent !important;
        --user-chunk-background-color: rgba(33, 31, 54, 0.8) !important;
        --color-thought-header: rgba(25, 25, 40, 0.7) !important;
        --color-thought-body: rgba(25, 25, 40, 0.7) !important;
        --color-surface-bright: transparent !important;
        --color-surface: transparent !important;
        --color-surface-dim: transparent !important;
        --color-surface-container: transparent !important;
        --color-surface-container-low: transparent !important;
        --color-surface-container-lowest: transparent !important;
        --color-surface-container-high: transparent !important;
        --color-surface-container-highest: transparent !important;
        --color-neutral-8: transparent !important;
        --color-neutral-10: transparent !important;
        --color-neutral-variant-10: transparent !important;
        --color-neutral-20: #171c2d !important;
        /* Sidebar item BG */
        /* Text Colors */
        --color-on-surface: #ffffff !important;
        --color-on-surface-variant: #d5d5dc !important;
    }
    /* Force high-level containers transparent */
    app-root,
    div.layout-wrapper,
    main.layout-main,
    mat-sidenav-container,
    .mat-drawer-container,
    mat-sidenav-content,
    .mat-drawer-content,
    div.header-container,
    footer.ng-star-inserted,
    ms-prompt-input-wrapper,
    router-outlet + * {
        background: none !important;
        background-color: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }

    /* --- 3. Shared Pseudo-Element Definitions --- */
    /* Highlight */
    .panel-highlight-pseudo::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        height: 1px;
        background: linear-gradient(90deg, rgba(168, 121, 255, 0) 0%, rgba(168, 121, 255, 0.45) 30%, rgba(168, 121, 255, 0.45) 70%, rgba(168, 121, 255, 0) 100%);
        z-index: 5;
        border-top-left-radius: inherit;
        border-top-right-radius: inherit;
    }
    /* Texture (Restored Gradients) */
    .panel-texture-pseudo::after {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: radial-gradient(circle at 15% 85%, rgba(168, 121, 255, 0.12), transparent 30%), radial-gradient(circle at 85% 15%, rgba(66, 133, 244, 0.10), transparent 25%);
        background-size: 100% 100%, 100% 100%;
        pointer-events: none;
        z-index: 3;
        border-radius: inherit;
        opacity: 1;
    }


    /* --- 4. BASE Panel Styling (Transparent BG + Border/Shadow) --- */
    nav.sidenav,
    div.chat-view-container,
    .config-panel {
        background: transparent !important;
        /* Shows body::after blur */
        border: 1px solid rgba(130, 130, 180, 0.30) !important;
        border-radius: 0.8rem !important;
        box-shadow: 0 10px 35px rgba(0, 0, 0, 0.45) !important;
        position: relative !important;
        z-index: 1 !important;
        overflow: hidden;
    }

    /* --- 4b. OVERLAY Panel Styling (Semi-Transparent BG + Border/Shadow + STRONG BLUR) --- */
    .input-area-container,
    .mat-mdc-menu-panel,
    .mat-mdc-select-panel,
    mat-dialog-container,
    .mat-mdc-text-field-wrapper {
        background: rgba(18, 21, 32, 0.82) !important;
        backdrop-filter: blur(25px) !important;
        /* <<< STRONG BLUR for overlays */
        -webkit-backdrop-filter: blur(25px) !important;
        border: 1px solid rgba(130, 130, 180, 0.35) !important;
        border-radius: 0.8rem !important;
        box-shadow: 0 8px 30px rgba(0, 0, 0, 0.50) !important;
        position: relative !important;
        z-index: 10 !important;
        /* High z-index */
        color: #ffffff !important;
        overflow: hidden;
    }

    /* Apply Pseudo-Elements to BOTH Base and Overlay Panels */
    nav.sidenav::before,
    div.chat-view-container::before,
    .config-panel::before,
    .input-area-container::before,
    .mat-mdc-menu-panel::before,
    .mat-mdc-select-panel::before,
    mat-dialog-container::before,
    .mat-mdc-text-field-wrapper::before {
        /* Apply highlight */
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        height: 1px;
        background: linear-gradient(90deg, rgba(168, 121, 255, 0) 0%, rgba(168, 121, 255, 0.45) 30%, rgba(168, 121, 255, 0.45) 70%, rgba(168, 121, 255, 0) 100%);
        z-index: 5;
        border-top-left-radius: inherit;
        border-top-right-radius: inherit;
    }
    nav.sidenav::after,
    div.chat-view-container::after,
    .config-panel::after,
    .input-area-container::after,
    .mat-mdc-menu-panel::after,
    .mat-mdc-select-panel::after,
    mat-dialog-container::after,
    .mat-mdc-text-field-wrapper::after {
        /* Apply texture */
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: radial-gradient(circle at 15% 85%, rgba(168, 121, 255, 0.12), transparent 30%), radial-gradient(circle at 85% 15%, rgba(66, 133, 244, 0.10), transparent 25%);
        background-size: 100% 100%, 100% 100%;
        pointer-events: none;
        z-index: 3;
        border-radius: inherit;
        opacity: 1;
    }
    /* Remove highlight from input area */
    .input-area-container::before {
        content: none !important;
    }

    /* Specific border/bg overrides */
    .input-area-container {
        border-radius: 0 0 0.8rem 0.8rem !important;
        border-top: 1px solid rgba(130, 130, 180, 0.30) !important;
        border-left: none !important;
        border-right: none !important;
        border-bottom: none !important;
        box-shadow: none !important;
        z-index: 2 !important;
    }
    .mat-mdc-text-field-wrapper {
        border-radius: 0.5rem !important;
        background: rgba(30, 35, 50, 0.80) !important;
        border-color: rgba(130, 130, 180, 0.30) !important;
    }
    .run-settings-header {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
        padding: 0.5rem 0 !important;
        margin-bottom: 0.5rem;
    }

    /* === Target Compare/Get Code State Layers === */
    .run-settings button.mat-mdc-button[aria-label="Get code"],
    .header-container .header-right button.mat-mdc-button.mat-unthemed:not([mattooltip*="Account"])/* Compare button */
    {
        background: transparent !important;
        background-color: transparent !important;
        border: none !important;
        box-shadow: none !important;
        --mat-text-button-state-layer-color: transparent !important;
        --mat-text-button-hover-state-layer-opacity: 0 !important;
        --mat-text-button-focus-state-layer-opacity: 0 !important;
        --mat-text-button-pressed-state-layer-opacity: 0 !important;
        --mat-text-button-ripple-color: rgba(255, 255, 255, 0.1) !important;
    }
    .run-settings button.mat-mdc-button[aria-label="Get code"] span.mat-mdc-button-touch-target,
    .header-container .header-right button.mat-mdc-button.mat-unthemed:not([mattooltip*="Account"]) span.mat-mdc-button-touch-target {
        background: transparent !important;
        background-color: transparent !important;
    }
    .run-settings button.mat-mdc-button[aria-label="Get code"]:hover,
    .header-container .header-right button.mat-mdc-button.mat-unthemed:not([mattooltip*="Account"]):hover {
        background: rgba(255, 255, 255, 0.1) !important;
        --mat-text-button-state-layer-color: transparent !important;
        --mat-text-button-hover-state-layer-opacity: 0 !important;
    }


    /* --- 5. Layout Adjustments & Internal Scroll --- */
    .mat-drawer-content,
    mat-sidenav-content {
        display: flex !important;
        flex-direction: row !important;
        padding: 0.5rem !important;
        height: 100vh;
        box-sizing: border-box;
    }
    nav.sidenav,
    .config-panel {
        margin: 0.5rem;
        display: flex;
        flex-direction: column;
        overflow: hidden !important;
    }
    nav.sidenav {
        flex: 0 0 280px;
    }
    .config-panel {
        flex: 0 0 320px;
    }
    div.chat-view-container {
        flex: 1 1 auto;
        margin: 0.5rem;
        display: flex;
        flex-direction: column;
        overflow: hidden;
    }

    /* Internal scroll containers */
    nav.sidenav > div,
    .config-panel > div,
    div.chat-view-container > ms-layout-major,
    div.chat-view-container > ms-layout-major > ms-autoscroll-container {
        background: transparent !important;
        flex-grow: 1;
        overflow-y: auto !important;
    }
    div.chat-view-container > ms-layout-major > ms-autoscroll-container {
        padding: 1rem 1.5rem !important;
    }

    /* Input Area Textarea */
    .input-area-container textarea {
        background: transparent !important;
        color: #ffffff !important;
        border: none !important;
        box-shadow: none !important;
        outline: none !important;
        height: auto;
        min-height: 24px;
        padding: 0.5rem !important;
        resize: none;
        flex-grow: 1;
        max-height: 200px;
        overflow-y: auto;
        scrollbar-width: thin;
        scrollbar-color: rgba(130, 130, 180, 0.35) rgba(19, 23, 35, 0.3);
        /* Font handled globally */
    }
    .input-area-container textarea::placeholder {
        color: rgba(255, 255, 255, 0.5) !important;
        opacity: 1 !important;
    }

    /* --- 6. NESTED Element Styling (Messages, Code Blocks, Thoughts, SysInstr) --- */
    /* Common styles for nested elements */
    ms-chat-turn[aria-label^="Model"] .message-bubble,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk,
    ms-code-block,
    ms-model-thought,
    .system-instruction-panel {
        backdrop-filter: blur(18px) !important;
        -webkit-backdrop-filter: blur(18px) !important;
        border-radius: 0.8rem !important;
        box-shadow: 0 4px 15px rgba(0, 0, 0, 0.35) !important;
        position: relative !important;
        z-index: 2 !important;
        margin-bottom: 1rem !important;
        padding: 1rem 1.5rem !important;
        overflow: hidden;
        border-style: solid !important;
        /* Ensure border is solid */
    }

    /* LLM Response Bubble */
    ms-chat-turn[aria-label^="Model"] .message-bubble {
        background: rgba(20, 24, 40, 0.90) !important;
        border-width: 1px !important;
        border-color: rgba(130, 130, 180, 0.45) !important;
        color: #d5d5dc !important;
    }

    /* User Message Chunk */
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk {
        background: rgba(28, 38, 60, 0.94) !important;
        border-width: 2px !important;
        border-color: #3c4768 !important;
        color: #ffffff !important;
        /* Base color */
    }
    /* Force user text color, overriding inherited black/grey */
    .user-prompt-container[_ngcontent-ng-c2569464191] .text-chunk[_ngcontent-ng-c2569464191],
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk .text-chunk-content,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > * {
        color: #ffffff !important;
    }
    /* Re-apply specific markdown colors *within* user messages */
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > strong,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > b {
        color: #ADD8E6 !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > em,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > i {
        color: #FFDAB9 !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > span.inline-code.ng-star-inserted {
        color: #98FB98 !important;
        background-color: rgba(0, 50, 0, 0.7) !important;
        border-color: rgba(0, 100, 0, 0.3) !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk .katex .katex-html,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk .katex .katex-html * {
        color: #ffc0cb !important;
    }


    /* Model Thoughts */
    ms-model-thought {
        background: rgba(8, 10, 15, 0.97) !important;
        border-width: 1px !important;
        border-color: #45474a !important;
        color: #d5d5dc !important;
    }
    ms-model-thought .header,
    ms-model-thought .content {
        background: transparent !important;
        color: inherit !important;
    }
    ms-model-thought .header {
        padding-bottom: 0.5rem;
        margin-bottom: 0.5rem;
        border-bottom: 1px solid rgba(130, 130, 180, 0.2);
        font-weight: 500;
    }
    ms-model-thought .content {
        padding-top: 0.5rem;
    }

    /* Code Blocks & System Instructions */
    ms-code-block,
    .system-instruction-panel {
        background: rgba(15, 17, 22, 0.95) !important;
        border-width: 1px !important;
        border-color: #3c4768 !important;
        color: #d5d5dc !important;
    }
    /* Restore default text color for Sys Instr, allow markdown overrides */
    .system-instruction-panel .mat-expansion-panel-body,
    .system-instruction-panel .mat-expansion-panel-body ms-cmark-node {
        color: #d5d5dc !important;
    }


    /* Apply Pseudo-Elements to ALL Nested Panels */
    ms-chat-turn[aria-label^="Model"] .message-bubble::before,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk::before,
    ms-code-block::before,
    ms-model-thought::before,
    .system-instruction-panel::before {
        /* Apply highlight */
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        height: 1px;
        background: linear-gradient(90deg, rgba(168, 121, 255, 0) 0%, rgba(168, 121, 255, 0.35) 30%, rgba(168, 121, 255, 0.35) 70%, rgba(168, 121, 255, 0) 100%);
        z-index: 5;
        border-top-left-radius: inherit;
        border-top-right-radius: inherit;
        opacity: 0.8;
    }
    ms-chat-turn[aria-label^="Model"] .message-bubble::after,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk::after,
    ms-code-block::after,
    ms-model-thought::after,
    .system-instruction-panel::after {
        /* Apply texture */
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: radial-gradient(circle at 15% 85%, rgba(168, 121, 255, 0.1), transparent 25%), radial-gradient(circle at 85% 15%, rgba(66, 133, 244, 0.08), transparent 20%);
        background-size: 100% 100%, 100% 100%;
        pointer-events: none;
        z-index: 3;
        border-radius: inherit;
        opacity: 1;
    }

    /* Subtle Hover effect - Border highlight only */
    ms-chat-turn[aria-label^="Model"] .message-bubble:hover,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk:hover,
    ms-code-block:hover,
    ms-model-thought:hover,
    .system-instruction-panel:hover {
        border-color: rgba(168, 121, 255, 0.65) !important;
        /* Highlight border on hover */
        outline: none !important;
    }


    /* Code Block Inner <pre> & Content Below */
    ms-code-block pre {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
        backdrop-filter: none !important;
        -webkit-backdrop-filter: none !important;
        padding: 0 !important;
        margin: 0 !important;
        overflow: visible !important;
    }
    ms-code-block pre code {
        background: transparent !important;
        color: #f8f8f2 !important;
        padding: 0 !important;
        display: block;
        /* Font handled globally */
        overflow-x: auto;
        scrollbar-width: thin;
        scrollbar-color: rgba(130, 130, 180, 0.35) rgba(10, 12, 18, 0.6);
    }
    ms-code-block {
        padding: 1em 1.5em !important;
        overflow: hidden;
    }

    /* Style elements BELOW code blocks */
    ms-code-block + ms-action-chip-list,
    div.code-block-footer {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
        backdrop-filter: none !important;
        -webkit-backdrop-filter: none !important;
        padding: 0.5rem 1.5rem 0rem 1.5rem !important;
        margin-top: -1rem !important;
        position: relative;
        z-index: 1;
        display: flex;
        align-items: center;
        flex-wrap: wrap;
        gap: 0.5rem;
    }
    ms-action-chip-list span.language-name,
    ms-action-chip-list span.disclaimer,
    div.code-block-footer span {
        color: #b0b5c0 !important;
        /* Font handled globally */
        background: rgba(40, 42, 54, 0.7) !important;
        padding: 0.2em 0.6em !important;
        border-radius: 4px !important;
        border: 1px solid rgba(130, 130, 180, 0.2);
    }
    ms-action-chip-list button {
        color: #b0b5c0 !important;
        background-color: rgba(40, 42, 54, 0.7) !important;
        border: 1px solid rgba(130, 130, 180, 0.2) !important;
        padding: 2px 6px !important;
        border-radius: 4px !important;
        line-height: 1;
        min-width: auto !important;
    }
    ms-action-chip-list button mat-icon {
        font-size: 1.1em;
        height: 1.1em;
        width: 1.1em;
        color: #b0b5c0 !important;
    }
    ms-action-chip-list button:hover {
        background-color: rgba(60, 62, 74, 0.8) !important;
        border-color: rgba(130, 130, 180, 0.4) !important;
        color: #ffffff !important;
    }
    ms-action-chip-list button:hover mat-icon {
        color: #ffffff !important;
    }

    /* --- 7. Dropdowns & Dialogs Internals --- */
    /* Font handled globally */
    .mat-mdc-menu-item,
    .mat-mdc-option,
    mat-dialog-container .mat-mdc-dialog-content,
    mat-dialog-container .mat-mdc-dialog-title,
    mat-dialog-container .mat-mdc-dialog-actions button .mdc-button__label {
        color: #ffffff !important;
        background: transparent !important;
    }
    .mat-mdc-menu-item:hover,
    .mat-mdc-option:hover,
    .mat-mdc-option.mdc-list-item--selected,
    mat-dialog-container .mat-mdc-dialog-actions button:hover {
        background-color: rgba(255, 255, 255, 0.1) !important;
    }
    get-code-dialog .code-container,
    get-code-dialog pre {
        background: rgba(0, 0, 0, 0.4) !important;
        border: 1px solid rgba(255, 255, 255, 0.15) !important;
        border-radius: 6px;
        padding: 1em;
        color: #f1f1f1 !important;
        overflow: auto;
    }
    get-code-dialog .code-container code {
        color: inherit !important;
    }

    /* --- 8. Buttons & Form Elements Readability (Borders Refined) --- */
    /* Font handled globally */
    button .mdc-button__label,
    button .mat-mdc-button-touch-target,
    a.mat-mdc-button .mdc-button__label {
        color: #ffffff !important;
        z-index: 1;
        position: relative;
    }
    button.mat-mdc-button,
    button.mat-mdc-icon-button,
    a.mat-mdc-button {
        border: none !important;
    }
    /* No borders on buttons */
    button.mat-primary,
    button[color="primary"] {
        background-color: #421c78 !important;
        color: #ffffff !important;
        transition: background-color 0.2s ease;
        border-radius: 6px !important;
    }
    button.mat-primary:hover,
    button[color="primary"]:hover {
        background-color: #8e24aa !important;
    }
    /* Exclude Compare/Get Code from this default styling */
    button.mat-mdc-button:not(.mat-primary):not([color="primary"]):not(.mat-mdc-icon-button):not(.mat-unthemed),
    a.mat-mdc-button:not(.mat-primary):not([color="primary"]) {
        background-color: rgba(255, 255, 255, 0.08) !important;
        color: #ffffff !important;
        transition: background-color 0.2s ease;
        border-radius: 6px !important;
    }
    button.mat-mdc-button:not(.mat-primary):not([color="primary"]):not(.mat-mdc-icon-button):not(.mat-unthemed):hover,
    a.mat-mdc-button:not(.mat-primary):not([color="primary"]):hover {
        background-color: rgba(255, 255, 255, 0.15) !important;
    }

    button.mat-mdc-icon-button {
        background: transparent !important;
    }
    button.mat-mdc-icon-button mat-icon {
        color: #d5d5dc !important;
        transition: color 0.2s ease;
    }
    button.mat-mdc-icon-button:hover mat-icon {
        color: #ffffff !important;
    }

    /* === Attempt to fix Run button text visibility === */
    .input-area-container button.mat-primary .mdc-button__label {
        color: #ffffff !important;
        opacity: 1 !important;
        z-index: 2 !important;
        /* Try slightly higher z-index */
        position: relative;
        /* Ensure positioning context */
    }

    /* --- 9. Specific Text Styles (Restored & Scoped) --- */
    /* Font handled globally */
    body ms-cmark-node > h1,
    body ms-cmark-node > h2,
    body ms-cmark-node > h3,
    body ms-cmark-node > h4,
    body ms-cmark-node > h5,
    body ms-cmark-node > h6 {
        color: #e14c4c !important;
    }
    body ms-cmark-node > strong,
    body ms-cmark-node > b {
        color: #7490d6 !important;
    }
    body ms-cmark-node > em,
    body ms-cmark-node > i {
        color: #ff79c6 !important;
    }
    body:not(ms-cmark-node) i,
    body:not(ms-cmark-node) em {
        color: #ff79c6 !important;
    }
    /* Global italic */
    body ms-cmark-node > span.inline-code.ng-star-inserted {
        color: #e86b6b !important;
        background-color: rgba(52, 52, 49, 0.6) !important;
        padding: 0.1em 0.3em;
        border-radius: 4px;
        /* font-size: 0.9em; */
        border: 1px solid rgba(255, 100, 100, 0.2);
    }
    .katex .katex-html,
    .katex .katex-html * {
        color: #ff79c6 !important;
    }

    /* Override specific markdown colors ONLY inside user messages */
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > strong,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > b {
        color: #ADD8E6 !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > em,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > i {
        color: #FFDAB9 !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk ms-cmark-node > span.inline-code.ng-star-inserted {
        color: #98FB98 !important;
        background-color: rgba(0, 50, 0, 0.7) !important;
        border-color: rgba(0, 100, 0, 0.3) !important;
    }
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk .katex .katex-html,
    ms-chat-turn[aria-label^="User"] ms-prompt-chunk .katex .katex-html * {
        color: #ffc0cb !important;
    }


    /* --- 10. Scrollbar --- */
    /* (Unchanged) */
    * {
        scrollbar-width: thin;
        scrollbar-color: rgba(168, 121, 255, 0.55) rgba(19, 23, 35, 0.3);
    }
    ::-webkit-scrollbar {
        width: 8px !important;
        height: 8px !important;
    }
    ::-webkit-scrollbar-track {
        background: rgba(19, 23, 35, 0.3) !important;
        border-radius: 4px !important;
    }
    ::-webkit-scrollbar-thumb {
        background: rgba(130, 130, 180, 0.45) !important;
        border-radius: 4px !important;
        border: 1px solid rgba(168, 121, 255, 0.2);
    }
    ::-webkit-scrollbar-thumb:hover {
        background: rgba(168, 121, 255, 0.65) !important;
    }

    /* --- 11. Final Overrides & Panel Headers --- */
    /* Font handled globally */
    nav.sidenav .nav-section-header,
    .config-panel .setting-section .header,
    .config-panel .mat-expansion-panel-header,
    .system-instruction-panel .mat-expansion-panel-header {
        background: rgba(16, 19, 28, 0.85) !important;
        backdrop-filter: blur(5px);
        -webkit-backdrop-filter: blur(5px);
        border-bottom: 1px solid rgba(130, 130, 180, 0.30) !important;
        color: #e8eeff !important;
        border-top-left-radius: inherit;
        border-top-right-radius: inherit;
        border-bottom-left-radius: 0 !important;
        border-bottom-right-radius: 0 !important;
        margin: 0 !important;
        flex-shrink: 0;
        position: sticky;
        top: 0;
        z-index: 4;
        height: auto !important;
        min-height: 48px;
        padding: 0.75rem 1.5rem !important;
    }
    /* Remove border when expansion panel is closed */
    .config-panel .mat-expansion-panel:not(.mat-expanded) .mat-expansion-panel-header,
    .system-instruction-panel:not(.mat-expanded) .mat-expansion-panel-header {
        border-bottom: none !important;
        border-bottom-left-radius: inherit !important;
        border-bottom-right-radius: inherit !important;
    }
    .system-instruction-panel .mat-expansion-panel-header h2.gmat-title-medium {
        color: #a971ff !important;
    }

    /* Remove focus overlay border */
    .mat-mdc-form-field-focus-overlay {
        display: none !important;
    }
    .mat-mdc-form-field-infix {
        border-top: none !important;
    }
}
